import React, { Component } from 'react';
import { View, Text, StyleSheet, ScrollView, Dimensions } from 'react-native';
import { createDrawerNavigator, DrawerContent } from '@react-navigation/drawer';
import { NavigationContainer } from '@react-navigation/native';
import Header from '../components/Header';
import DrawerHome from '../screens/drawer/DrawerHome';
import Rewards from '../screens/drawer/Rewards';
import CustomDrawer from '../components/CustomDrawer';


const Drawer = createDrawerNavigator();
import {AntDesign} from '@expo/vector-icons';

export default class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
    };
  }

  render() {
    return (
            <NavigationContainer independent={true} >
                <Drawer.Navigator
                 initialRouteName="Home" 
                 drawerContent={props => CustomDrawer(props)}
                 drawerStyle={{
                  backgroundColor: '#fff',
                  width: 300,
                }}
                drawerContentOptions={{
                  activeTintColor: '#297ca0',
                  itemStyle: { marginTop: 3, borderRadius: 20, marginHorizontal: 30 },
                  labelStyle: {color: '#758184'}
                }} 
                
                >
                  <Drawer.Screen name="Home" component={DrawerHome}  />
                  <Drawer.Screen name="Goals & Rewards"  component={Rewards} />
                  <Drawer.Screen name="My Favorites" component={DrawerHome}  />
                  <Drawer.Screen name="My Approvals" component={DrawerHome}  />
                  <Drawer.Screen name="Scheduled Transactions" component={DrawerHome}  />
                  <Drawer.Screen name="Transactions Hostory" component={DrawerHome}  />
                  <Drawer.Screen name="Promotions" component={DrawerHome}  />
                  <Drawer.Screen name="Fee Details" component={DrawerHome}  />
                  <Drawer.Screen name="Help/FAQ" component={DrawerHome}  />

                </Drawer.Navigator>
            </NavigationContainer>
    );
  }
}


const styles = StyleSheet.create({
    container:{
        flex: 1,
        backgroundColor: 'white'
    },
    qrScannerStyle:{
      position: 'absolute',
      //bottom: -80,
      height: 60,
      width: 60,
      alignSelf: 'center'
    },
    contentContainer:{
      flex: 1,
      //marginBottom: 80,
    },
    sliderContainer:{
      flex: 3.5,
      // alignItems: 'center',
      // justifyContent: 'center',
      backgroundColor: '#25AC5A',
    },
    gridContainer:{
      flex:4,
      //backgroundColor: 'yellow'
    },
    grid:{
      justifyContent: 'center',
      flexDirection: 'row',
      flexWrap: 'wrap',
      alignItems: 'flex-start',
      //backgroundColor: "blue",
      //marginTop: 100
    },
});
